import React, { Component } from "react";

import "../styles.css";
import { connect } from "react-redux";

import CartItem from "./CartItem";

class Cart extends Component {
  // getTotal() {
  //   const { cartGoods } = this.props;
  //   return cartGoods.reduce((acc, item) => acc + item.price * item.amount, 0);
  // }

  render() {
    return (
      <div className="cart">
        <h2 className="cart__title">Shopping Cart</h2>
        {this.props.cartGoods.length ? (
          <ul className="cart__list">
            {this.props.cartGoods.map((item) => (
              <li className="cart__list-item" key={item.id}>
                <CartItem {...item} />
              </li>
            ))}
          </ul>
        ) : (
          <p className="cart__note">Nothing in the cart now</p>
        )}

        <p className="cart__total">Total: {this.props.total()}.00$</p>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    cartGoods: state.cart,
  };
};

const mapDispatchToProps = (state) => {
  return {
    total: () => dispatch(getTotal()),
  };
};

export default connect(mapStateToProps)(Cart);
